<div class="modal-body">    <div class="row">        <div class="col-md-10">            <div id="delete_order_success" class="alert alert-success" style="display:none;">Order cancel successfully.</div>            <div id="delete_order_error" class="alert alert-danger" style="display:none;">Failed to cancel order details.</div>        </div>    </div>    <div class="row">        <div class="col-md-10">            <input type="hidden" id="delete_order_id" name="delete_order_id" value="<?php echo $order_id ?>">            <p>Order Number : <?php echo $order_no; ?> <br>Are you sure want to delete this order? </p>        </div>    </div></div><div class="modal-footer">    <button type="submit" class="btn blue">Yes</button>    <button type="button" data-dismiss="modal" class="btn default">No</button></div><script>    $(document).ready(function () {        $('#deleteorder_form').submit(function (e) {            e.preventDefault();            var formdata = {                delete_order_id: $('#delete_order_id').val()            };            $.ajax({                url: "<?php echo site_url('Admin/delete_order') ?>",                type: 'post',                data: formdata,                success: function (msg) {                    if (msg == 'fail') {                        $('#delete_order_error').show();                    }                    if (msg == 'success') {                        $('#delete_order_success').show();                        window.location.reload();                    }                }            });        });    });</script>