<?php$client_id = $this->uri->segment(3);$get_user = array(    'User_Id' => $client_id,    'Status' => 1);$this->db->where($get_user);$q_user = $this->db->get('tbl_user');foreach ($q_user->result() as $row_user) {    $Username = $row_user->Username;}$get_transaction = array(    'User_Id' => $client_id,    'Status' => 1);$this->db->select_sum('Paid_Amt');$this->db->where($get_transaction);$q_transaction = $this->db->get('tbl_transaction');$result_transaction = $q_transaction->result();$plan_amount1 = $result_transaction[0]->Paid_Amt;$plan_amount = number_format((float) $plan_amount1, 2, '.', '');?><!-- BEGIN CONTENT --><div class="page-content-wrapper">    <div class="page-content">        <div class="row">            <div class="col-md-12">                <div class="portlet box blue">                    <div class="portlet-title">                        <div class="caption">                            <i class="icon-layers"></i>Account History - <?php echo $Username; ?>                        </div>                        <div class="tools hidden-xs">                            <div class="col-md-12">                                <h4 id="plantotal_head"><strong>Total Plan Amount : $<?php echo $plan_amount; ?></strong></h4>                            </div>                        </div>                    </div>                    <div class="portlet-body">                        <table class="sample_2 table table-hover" id="sample_2">                            <thead>                                <tr>                                    <th>Plan Date</th>                                    <th>Plan Name</th>                                    <th>Plan Amount ($)</th>                                     <th>Balance Amount($)</th>                                </tr>                            </thead>                            <tbody>                                <?php                                $get_account = array(                                    'User_Id' => $client_id,                                    'Status' => 1                                );                                $this->db->where($get_account);                                $this->db->order_by('Acc_Id', 'asc');                                $q_account = $this->db->get('tbl_account');                                foreach ($q_account->result() as $row_account) {                                    $Txn_Id = $row_account->Txn_Id;                                    $Order_Date1 = $row_account->Order_Date;                                    $Order_Date = date("d-M-Y", strtotime($Order_Date1));                                    $Order_Amt = $row_account->Total_Amount;                                                                        $this->db->where('txn_id', $Txn_Id);                                    $q_cart = $this->db->get('tbl_cart');                                    foreach ($q_cart->result() as $row_cart) {                                        $plan_id = $row_cart->Plan_Id;                                    }                                    $get_plan = array(                                        'Plan_Id' => $plan_id                                    );                                    $this->db->where($get_plan);                                    $q_plan = $this->db->get('tbl_plan');                                    foreach ($q_plan->result() as $row_plan) {                                        $plan_name = $row_plan->Plan_Name;                                        $plan_type = $row_plan->Type;                                    }                                    $plan_amount1 = $plan_amount - $Order_Amt;                                    $plan_amount = number_format((float) $plan_amount1, 2, '.', '');                                    ?>                                    <tr class="odd gradeX">                                        <td><?php echo $Order_Date; ?></td>                                        <td><?php echo $plan_name .' '.$plan_type; ?></td>                                        <td><?php echo $Order_Amt; ?></td>                                        <td><?php echo $plan_amount; ?></td>                                    </tr>                                    <?php                                }                                ?>                            </tbody>                        </table>                    </div>                </div>            </div>        </div>        <!-- END PAGE CONTENT INNER -->    </div></div><!-- END CONTENT -->