<div class="modal-body">    <div class="row">        <div class="col-md-10">            <div id="client_delete_order_success" class="alert alert-success" style="display:none;">Order cancel successfully.</div>            <div id="client_delete_order_error" class="alert alert-danger" style="display:none;">Failed to cancel order details.</div>        </div>    </div>    <div class="row">        <div class="col-md-10">            <input type="hidden" id="delete_order_id" name="delete_order_id" value="<?php echo $order_id ?>">            <h4>Are you sure want to cancel this order? <br> Order Number : <?php echo $order_no; ?>  </h4>        </div>    </div>    <div class="form-group">        <label class="col-md-4 control-label">Reason for Canceling<span class="required"> *</span></label>        <div class="col-md-8">            <div class="form-group form-md-line-input has-info">                <input type="text" class="form-control input-sm" data-validate="required" id="cancel_reason" name="cancel_reason" autocomplete="off" data-message-required="Please Enter Reason">            </div>        </div>    </div></div><div class="modal-footer">    <button type="submit" class="btn blue">Yes</button>    <button type="button" data-dismiss="modal" class="btn default">No</button></div><script>    $(document).ready(function () {        $('#delete_client_orderinfo_form').submit(function (e) {            e.preventDefault();            var formdata = {                delete_order_id: $('#delete_order_id').val(),                reason: $('#cancel_reason').val()            };            $.ajax({                url: "<?php echo site_url('Client/delete_order') ?>",                type: 'post',                data: formdata,                success: function (msg) {                    if (msg == 'fail') {                        $('#client_delete_order_error').show();                    }                    if (msg == 'success') {                        $('#client_delete_order_success').show();                        window.location.reload();                    }                }            });        });    });</script>