<?php$client_id = $this->session->userdata('client_id');$get_refund = array(    'R_Id' => $refund_id,    'User_Id' => $client_id,    'Status' => 1);$this->db->where($get_refund);$q_refund = $this->db->get('tbl_refund');foreach ($q_refund->result() as $row_refund) {    $plan_id = $row_refund->Plan_Id;    $get_plan = array(        'Plan_Id' => $plan_id    );    $this->db->where($get_plan);    $q_plan = $this->db->get('tbl_plan');    foreach ($q_plan->result() as $row_plan) {        $plan_name = $row_plan->Plan_Name;        $order_type = $row_plan->Type;    }    $Reason = $row_refund->Reason;    $Refund_Status = $row_refund->Refund_Status;    $Amount1 = $row_refund->Amount;    $Amount = number_format((float) $Amount1, 2, '.', '');    $Remarks = $row_refund->Remarks;}?><div class="modal-body">    <div class="row">        <div class="form-group">            <label class="control-label col-md-4"><strong>Plan Name : </strong></label>            <div class="col-md-8"><?php echo $plan_name . " ($order_type)"; ?>            </div>        </div>        <div class="form-group">            <label class="control-label col-md-4"><strong>Reason : </strong></label>            <div class="col-md-8"><?php echo $Reason; ?>            </div>        </div>        <div class="form-group">            <label class="control-label col-md-4"><strong>Status : </strong></label>            <div class="col-md-8"><?php echo $Refund_Status; ?>            </div>        </div>        <div class="form-group">            <label class="control-label col-md-4"><strong>Refund Amount : </strong></label>            <div class="col-md-8"><?php echo "$ " .$Amount; ?>            </div>        </div>        <div class="form-group">            <label class="control-label col-md-4"><strong>Remarks : </strong></strong></label>            <div class="col-md-8"><?php echo $Remarks; ?>            </div>        </div>